var contacts=document.querySelector(".contacts-button"),modal=document.querySelector(".modal-letter"),close=document.querySelector(".modal-close"),nameLetter=document.querySelector(".modal-input-name"),email=modal.querySelector("[name=email]"),letter=modal.querySelector("[name=letter]"),form=modal.querySelector("form"),isStorageSupport=!0,storage="";try{storage=localStorage.getItem("login")}catch(e){isStorageSupport=!1}contacts.addEventListener("click",function(e){e.preventDefault(),modal.classList.add("modal-show"),storage&&(email.value=storage),nameLetter.focus()}),close.addEventListener("click",function(e){e.preventDefault(),modal.classList.remove("modal-show")}),form.addEventListener("submit",function(e){nameLetter.value&&email.value&&letter.value?isStorageSupport&&localStorage.setItem("email",email.value):(e.preventDefault(),console.log("Нужно заполнить все поля"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),modal.classList.contains("modal-show")&&modal.classList.remove("modal-show"))});